func main() {
	floor := 0
	firstTimeEnteringBasement := -1

	for i, ch := range input {
		switch ch {
		case '(':
			floor++
		case ')':
			floor--
		default:
			fmt.Printf("Warning: unexpected character (index %v): %v\n", i, ch)
		}

		if floor == -1 && firstTimeEnteringBasement == -1 {
			firstTimeEnteringBasement = i + 1
		}
	}

	if firstTimeEnteringBasement == -1 {
		fmt.Printf("Santa never entered the basement\n")
	} else {
		fmt.Printf("Santa entered the basement on instruction: %v\n", firstTimeEnteringBasement)
	}

	fmt.Printf("Santa ended up on floor: %v\n", floor)
}
